mapper.select:
select
  id, order_id, product_name, product_number, create_time, update_time
from mmall.order


        and id = #{id}


        and order_id = #{orderId}


        and product_name = #{productName}


        and product_number = #{productNumber}


        and create_time = #{createTime}


        and update_time = #{updateTime}
